Ext.define('TeamDomain.store.FileDataStoreA', {
    extend: 'Ext.data.Store',
    requires: [
        'TeamDomain.model.FileData'
    ],
    constructor: function (cfg) {
        var me = this;
        cfg = cfg || {};
        me.callParent([Ext.apply({
                model: 'TeamDomain.model.FileData',
                storeId: 'FileDataStoreA',
                proxy: {
                    type: 'ajax',
                    url: 'spin/file_listA.sfl',
                    reader: {
                        type: 'json',
                        root: 'files'
                    }
                },
                listeners: {
                    load: function ()
                    {
                        var domain_panel = Ext.getCmp('domainGridPanelA');
                        domain_panel.enable();
                        domain_panel.showHorizontalScroller();
                        var folder_panel_a = Ext.getCmp('folderPanelA');
                        if(!folder_panel_a.isHidden())
                        {
                            folder_panel_a.showHorizontalScroller();
                        }
                        else
                        {
                            Ext.getCmp('folderPanelB').showHorizontalScroller();
                        }
                        var parent=document.getElementById('loading');
                        var logo=document.getElementById('logo');
                        if(logo!==null)
                        {
                            parent.removeChild(logo);
                        }
                    }
                }
            }, cfg)]);
    }
});